var car = "Em português: carro";
let bus = "Em português: ônibus";
const airplane = "Em português: avião";

/* A variável let respeita o escopo de bloco
A variável var não respeita o escopo de bloco
A variável const se mantém constante e não pode ser alterada*/

console.log("In english: car | " + car);
console.log("In english: bus | " + bus);
console.log("In english: airplane | " + airplane);